document.getElementById("form_focus").addEventListener("click",()=>{document.querySelector("#contact-form-name").scrollIntoView({behavior:"smooth",block:"center"})});document.addEventListener("scroll",function(e){window.scrollY+window.innerHeight>1e3&&document.getElementById("mynav").classList.add("bg-gray-900")});document.addEventListener("scroll",function(e){window.scrollY+window.innerHeight<1e3&&document.getElementById("mynav").classList.remove("bg-gray-900")});document.getElementById("nav-toggle").onclick=function(){document.getElementById("nav-content").classList.toggle("hidden")};
